import React from 'react';
import { useParams, Link } from 'react-router-dom';
import './EventExpand.css'; // Make sure you create this CSS file // Make sure you create this image file
import eventImage from './components/img/mockEvent1.png';

// Placeholder for mock data. You should fetch the actual data based on the event ID.
const mockEvent = {
  id: '1',
  title: 'Separation and Divorce Support - Meet and Greet',
  host: 'Jake L.',
  dateTime: '11 Feb 2024, 07:00 PM',// Replace with path to your image.
  details: `Let's chat! I'm hosting a low-key event to get to know you all a little better. During our Meet and Greet, we can discuss the emotional quagmires we may currently find ourselves in, create conversation about how best to make it through tough adjustments, or just chat about anything that might take our minds off real life for a little while. See you there!`,
  tags: ['Single Parents', 'Marital Separation Support', 'Divorce Support']
};


const EventExpand = () => {
  let { id } = useParams(); // This hook allows you to grab the event ID from the URL
  // TODO: Replace with logic to fetch event data based on `id`.
  const event = mockEvent; // This is a placeholder

  return (
    <div className="event-expand">
      <header className="event1-header">
        <h1 className="event1-title">{event.title}</h1>
        <p className="event-host">{event.host}</p>
      </header>
      <div className="event-body">
        <img src={event.image} alt={event.title} className="event-image" />
        <div className="event-details">
          <p className="event-date-time">{event.dateTime}</p>
          <p className="event-location">{event.location}</p>
          <p>{event.details}</p>
        </div>
      </div>
    </div>
  );
};

export default EventExpand;
