import React from 'react';
import { useParams, Link } from 'react-router-dom';
import './EventExpand.css'; // Make sure you create this CSS file // Make sure you create this image file
import eventImage from './components/img/mockEvent1.png';


// Placeholder for mock data. You should fetch the actual data based on the event ID.
const mockEvent = {
  id: '1',
  title: "Exploring Banff National Park",
    image: eventImage,
    place: "Banff National Park",
    host: "AMC Worcester 3RD",
    time: "Wed, Feb 21 - 6:30 PM EST",
    details: "Join us for a hike through the beautiful Banff National Park. We will be exploring the trails and taking in the breathtaking views. This event is open to all skill levels. We hope to see you there!",
    going: 8,
    price: "Free",
};


const EventExpand = () => {
  let { id } = useParams(); // This hook allows you to grab the event ID from the URL
  // TODO: Replace with logic to fetch event data based on `id`.
  const event = mockEvent; // This is a placeholder

  return (
    <div className="event-expand">
      <header className="event1-header">
        <h1 className="event1-title">{event.title}</h1>
        <p className="event1-host"> 
        <img src={eventImage} alt= 'user-pfp' className="pfp" />
        Hosted By {event.host}</p>
      </header>
      <div className="event1-body">
        <img src={eventImage} alt={event.title} className="event1-image" />
        <div className="event1-details">
          <p className="event1-date-time">{event.dateTime}</p>
          <p className="event1-location">{event.location}</p>
          <p>{event.details}</p>
        </div>
      </div>
    </div>
  );
};

export default EventExpand;
