import React from 'react';
import { useParams } from 'react-router-dom';
import './EventExpand.css'; // Assume you will create a corresponding CSS file
import mockEvent1 from './components/img/mockEvent1.jpg'; // Replace with actual image import

const EventExpand = () => {
  let { id } = useParams(); 
  const event =  {
    title: "Exploring Banff National Park",
    image: mockEvent1,
    place: "Banff National Park",
    host: "AMC Worcester 3RD",
    time: "Wed, Feb 21 - 6:30 PM EST",
    going: 8,
    price: "Free",
  }; 

  return (
    <div className="event-expand">
      <h1>{event.title}</h1>
      {/* Replace the placeholders with actual event data */}
      <img src={event.image} alt={event.title} />
      <p>Hosted by: {event.host}</p>
      <p>Date & Time: {event.time}</p>
      <p>Location: {event.location}</p>
      <p>{event.description}</p>
      {/* ... Add more event details ... */}
    </div>
  );
};

export default EventExpand;
