import { useParams } from 'react-router-dom';
import './EventExpand.css'; // Assume you will create a corresponding CSS file

const EventExpand = () => {
  let { id } = useParams(); // This hook allows you to grab the event ID from the URL
  const event = {}; // Replace with event data fetching logic based on the ID

  // Fetch the event details from your data source (API, state management, etc.)
  // using the `id`. For now, we'll use an empty object.

  return (
    <div className="event-expand">
      <h1>{event.title}</h1>
      {/* Replace the placeholders with actual event data */}
      <img src={event.image} alt={event.title} />
      <p>Hosted by: {event.host}</p>
      <p>Date & Time: {event.time}</p>
      <p>Location: {event.location}</p>
      <p>{event.description}</p>
      {/* ... Add more event details ... */}
    </div>
  );
};

export default EventExpand;
