import React from 'react';
import { useParams } from 'react-router-dom';
import './EventExpand.css'; // Make sure you create this CSS file
import eventImage from './components/img/mockEvent1.png'; // Make sure this path is correct

const mockEvent = {
  id: '1',
  title: "Exploring Banff National Park",
  image: eventImage,
  place: "Banff National Park",
  host: "AMC Worcester 3RD",
  dateTime: "Wed, Feb 21 - 6:30 PM EST",
  details: "Join us for a hike through the beautiful Banff National Park. We will be exploring the trails and taking in the breathtaking views. This event is open to all skill levels. We hope to see you there!",
  going: 8,
  price: "Free",
};

const EventExpand = () => {
  let { id } = useParams();
  const event = mockEvent; // Replace with data fetching logic

  return (
    <div className="event-expand">
      <header className="event1-header">
        <div className="header-content">
          <img src={event.image} alt="Event" className="event1-image" />
          <div className="event-host-info">
            <h1 className="event1-title">{event.title}</h1>
            <p className="event1-host">Hosted By</p>
            <p className="event1-host-name">{event.host}</p>
          </div>
        </div>
      </header>
      <div className="event1-body">
        {/* ... */}
      </div>
    </div>
  );
};

export default EventExpand;
